script:
//Agregar un filtro al campo product_segment de este formulario
//El campo categoria_producto (declarado en el doctype Segmentos de Productos)
//retorna valores filtrados por valor que del campo product_category de este formulario

cur_frm.fields_dict['segment'].get_query = function(doc) {
	return {
		filters: {
			"categoria_producto": doc.category
		}
	}
}


cur_frm.fields_dict['subcategory'].get_query = function(doc) {
	return {
		filters: {
			"segmento_producto": doc.segment
		}
	}
}
$("<div id='mapa'></div>").appendTo(cur_frm.get_field("mapa").wrapper);
$("<script async defer src='https://maps.googleapis.com/maps/api/js?key=AIzaSyADLmtXOTB4Is896vv62WTRdS2cB8LCQmM&callback=initMap'>/script>").appendTo(cur_frm.get_field("mapa").wrapper);

$("<script>function initMap() {   var mapDiv = document.getElementById('mapa'); var map = new google.maps.Map(mapDiv, { center: {lat: 2.93755, lng: -75.29660}, zoom: 12 });var marker = new google.maps.Marker({ position: {lat: 44.540, lng: -75.28917}, map: map });map.addListener('click', function(e) {cur_frm.set_value('longitud',e.latLng.lng().toFixed(5)); cur_frm.set_value('latitud',e.latLng.lat().toFixed(5));marker.setMap(null);marker = new google.maps.Marker({ position: new google.maps.LatLng(e.latLng.lat(),e.latLng.lng()), map: map})});}</script>").appendTo(cur_frm.get_field("mapa").wrapper);

$("<style> #mapa {width: 100%; height: 400px; } </style>").appendTo(cur_frm.get_field("mapa").wrapper);

//Agregar un filtro al campo product_segment de este formulario
